package selectionstatement;

public class IfDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=10;
		if(x==20) {
		System.out.println("hello");
		}
		else {
		System.out.println("hi");
     	}
		
		boolean b=true;
		if(b==false) {   //b=false(assigns) b==false(compares)
			System.out.println("Hello");
		}
		else {
		System.out.println("hi");
		}
}
}